import React,{useState} from 'react';
import styled from '@emotion/styled';

const Campo = styled.div`
    display: flex;
    margin-bottom: 1rem; /** Distancia */
    align-items: center;
`;

const Label = styled.label`
    flex: 0 0 100px;
`;

const Select = styled.select`
    display: block;
    background-color: #ffffff;
    width: 100%;
    padding: 1rem;
    border: 1px solid #e1e1e1;
    -webkit-appearance: none;
`;

const InputRadio = styled.input`
    margin: 0  1rem;
`;

const Boton = styled.button`
    background-color: #00838F;
    font-size: 16px;
    width: 100%;
    padding: 1rem;
    text-transform: uppercase;
    font-weight: bold;
    border: none;
    margin-top: 2rem; /** Distancia con la caja de arriba */
    transition: background-color .3s ease; /** Transicion al momento de hacer hover con los segundos */

    &:hover {
        background-color: #26C6DA;
        cursor: pointer; /** Cursor de "mano" */
    }

`;

const Formulario = () => {

    const [datos,guardarDatos] = useState({
        marca:'',
        year:'',
        plan:''
    });

    const [error,guardarError] = useState(false);

    // Extraer los valores del state
    const {marca,year,plan} = datos;

    // Leer los datos del formulario y colocarlos en el state
    const obtenerInformacion = e => {
        guardarDatos ({
            ...datos,
            [e.target.name] : e.target.value
        })
    };

    // Cuando el usuario presiona submit
    const cotizarSeguros = e => {
        e.preventDefault();

        // Validar
        if(marca.trim()==='' || year.trim()==='' || plan.trim()===''){
            guardarError(true);
            return;
        }

        guardarError(false);    

    };

    return ( 
        <form
            onSubmit={cotizarSeguros}
        >
            <Campo>
                <Label>Marca</Label>
                    <Select
                        name="marca"
                        value={marca} 
                        onChange={obtenerInformacion}
                    >    
                        <option value="">-- Seleccione --</option>
                        <option value="americano">Americano</option>
                        <option value="europeo">Europeo</option>
                        <option value="asiatico">Asiatico</option>
                    </Select> 
            </Campo>

            <Campo>
                <Label>Año</Label>
                    <Select
                        name="year"
                        value={year}
                        onChange={obtenerInformacion}
                    >    
                        <option value="">-- Seleccione --</option>
                        <option value="2021">2021</option>
                        <option value="2020">2020</option>
                        <option value="2019">2019</option>
                        <option value="2018">2018</option>
                        <option value="2017">2017</option>
                        <option value="2016">2016</option>
                        <option value="2015">2015</option>
                        <option value="2014">2014</option>
                        <option value="2013">2013</option>
                        <option value="2012">2012</option>
                    </Select> 
            </Campo>

            <Campo>
                <InputRadio 
                    type="radio" 
                    name="plan" 
                    value="basico"
                    checked={plan === "basico"}
                    onChange={obtenerInformacion} 
                /> Basico

                <InputRadio 
                    type="radio" 
                    name="plan" 
                    value="completo"
                    checked={plan === "completo"}
                    onChange={obtenerInformacion} 
                /> Completo
            </Campo>
            <Boton type="submit">Cotizar</Boton>
        </form>
     );
}

export default Formulario;
